"use client";

import { LayoutGridIcon, PlusIcon, SearchIcon } from "lucide-react";
import { useId, useState } from "react";

import InfoMenu from "@/registry/default/components/navbar-components/info-menu";
import NotificationMenu from "@/registry/default/components/navbar-components/notification-menu";
import SettingsMenu from "@/registry/default/components/navbar-components/settings-menu";
import { Button } from "@/registry/default/ui/button";
import { Input } from "@/registry/default/ui/input";
import { Label } from "@/registry/default/ui/label";
import { Switch } from "@/registry/default/ui/switch";

export default function Component() {
  const id = useId();
  const [checked, setChecked] = useState<boolean>(true);

  return (
    <header className="border-b px-4 md:px-6">
      <div className="flex h-16 items-center justify-between gap-4">
        {/* Left side */}
        <div className="relative flex-1">
          <Input
            className="peer h-8 w-full max-w-xs ps-8 pe-2"
            id={`input-${id}`}
            placeholder="Search..."
            type="search"
          />
          <div className="pointer-events-none absolute inset-y-0 start-0 flex items-center justify-center ps-2 text-muted-foreground/80 peer-disabled:opacity-50">
            <SearchIcon size={16} />
          </div>
        </div>
        {/* Right side */}
        <div className="flex items-center gap-4">
          {/* Test mode */}
          <div className="inline-flex items-center gap-2 max-md:hidden">
            <Label className="font-medium text-sm" htmlFor={`switch-${id}`}>
              Test mode
            </Label>
            <Switch
              aria-label="Toggle switch"
              checked={checked}
              className="data-[state=checked]:[&_span]:rtl:-translate-x-3 h-5 w-8 [&_span]:size-4 data-[state=checked]:[&_span]:translate-x-3"
              id={`switch-${id}`}
              onCheckedChange={setChecked}
            />
          </div>
          <div className="flex items-center gap-2">
            {/* Layout button */}
            <Button
              aria-label="Open layout menu"
              className="size-8 rounded-full text-muted-foreground shadow-none"
              size="icon"
              variant="ghost"
            >
              <LayoutGridIcon aria-hidden="true" size={16} />
            </Button>
            {/* Info menu */}
            <InfoMenu />
            {/* Notification */}
            <NotificationMenu />
            {/* Settings */}
            <SettingsMenu />
          </div>
          {/* Add button */}
          <Button
            aria-label="Add new item"
            className="size-8 rounded-full"
            size="icon"
          >
            <PlusIcon aria-hidden="true" size={16} />
          </Button>
        </div>
      </div>
    </header>
  );
}

"use client";

import { BellIcon } from "lucide-react";
import { useState } from "react";

import { Button } from "@/registry/default/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/registry/default/ui/popover";

const initialNotifications = [
  {
    action: "requested review on",
    id: 1,
    target: "PR #42: Feature implementation",
    timestamp: "15 minutes ago",
    unread: true,
    user: "Chris Tompson",
  },
  {
    action: "shared",
    id: 2,
    target: "New component library",
    timestamp: "45 minutes ago",
    unread: true,
    user: "Emma Davis",
  },
  {
    action: "assigned you to",
    id: 3,
    target: "API integration task",
    timestamp: "4 hours ago",
    unread: false,
    user: "James Wilson",
  },
  {
    action: "replied to your comment in",
    id: 4,
    target: "Authentication flow",
    timestamp: "12 hours ago",
    unread: false,
    user: "Alex Morgan",
  },
  {
    action: "commented on",
    id: 5,
    target: "Dashboard redesign",
    timestamp: "2 days ago",
    unread: false,
    user: "Sarah Chen",
  },
  {
    action: "mentioned you in",
    id: 6,
    target: "coss.com open graph image",
    timestamp: "2 weeks ago",
    unread: false,
    user: "Miky Derya",
  },
];

function Dot({ className }: { className?: string }) {
  return (
    <svg
      aria-hidden="true"
      className={className}
      fill="currentColor"
      height="6"
      viewBox="0 0 6 6"
      width="6"
      xmlns="http://www.w3.org/2000/svg"
    >
      <circle cx="3" cy="3" r="3" />
    </svg>
  );
}

export default function NotificationMenu() {
  const [notifications, setNotifications] = useState(initialNotifications);
  const unreadCount = notifications.filter((n) => n.unread).length;

  const handleMarkAllAsRead = () => {
    setNotifications(
      notifications.map((notification) => ({
        ...notification,
        unread: false,
      })),
    );
  };

  const handleNotificationClick = (id: number) => {
    setNotifications(
      notifications.map((notification) =>
        notification.id === id
          ? { ...notification, unread: false }
          : notification,
      ),
    );
  };

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button
          aria-label="Open notifications"
          className="relative size-8 rounded-full text-muted-foreground shadow-none"
          size="icon"
          variant="ghost"
        >
          <BellIcon aria-hidden="true" size={16} />
          {unreadCount > 0 && (
            <div
              aria-hidden="true"
              className="absolute top-0.5 right-0.5 size-1 rounded-full bg-primary"
            />
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80 p-1">
        <div className="flex items-baseline justify-between gap-4 px-3 py-2">
          <div className="font-semibold text-sm">Notifications</div>
          {unreadCount > 0 && (
            <button
              className="font-medium text-xs hover:underline"
              onClick={handleMarkAllAsRead}
              type="button"
            >
              Mark all as read
            </button>
          )}
        </div>
        <div
          aria-orientation="horizontal"
          className="-mx-1 my-1 h-px bg-border"
          role="separator"
          tabIndex={-1}
        />
        {notifications.map((notification) => (
          <div
            className="rounded-md px-3 py-2 text-sm transition-colors hover:bg-accent"
            key={notification.id}
          >
            <div className="relative flex items-start pe-3">
              <div className="flex-1 space-y-1">
                <button
                  className="text-left text-foreground/80 after:absolute after:inset-0"
                  onClick={() => handleNotificationClick(notification.id)}
                  type="button"
                >
                  <span className="font-medium text-foreground hover:underline">
                    {notification.user}
                  </span>{" "}
                  {notification.action}{" "}
                  <span className="font-medium text-foreground hover:underline">
                    {notification.target}
                  </span>
                  .
                </button>
                <div className="text-muted-foreground text-xs">
                  {notification.timestamp}
                </div>
              </div>
              {notification.unread && (
                <div className="absolute end-0 self-center">
                  <span className="sr-only">Unread</span>
                  <Dot />
                </div>
              )}
            </div>
          </div>
        ))}
      </PopoverContent>
    </Popover>
  );
}

import { SettingsIcon } from "lucide-react";

import { Button } from "@/registry/default/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/registry/default/ui/dropdown-menu";

export default function SettingsMenu() {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          aria-label="Open edit menu"
          className="rounded-full shadow-none"
          size="icon"
          variant="ghost"
        >
          <SettingsIcon
            aria-hidden="true"
            className="text-muted-foreground"
            size={16}
          />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className="max-w-64">
        <DropdownMenuItem>Appearance</DropdownMenuItem>
        <DropdownMenuItem>Preferences</DropdownMenuItem>
        <DropdownMenuItem>API Settings</DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}
