import { SparklesIcon, UploadIcon } from "lucide-react";

import AppToggle from "@/registry/default/components/navbar-components/app-toggle";
import TeamSwitcher from "@/registry/default/components/navbar-components/team-switcher";
import { Button } from "@/registry/default/ui/button";

const teams = ["Acme Inc.", "coss.com", "Junon"];

export default function Component() {
  return (
    <header className="border-b px-4 md:px-6">
      <div className="flex h-16 items-center justify-between gap-4">
        {/* Left side */}
        <div className="flex flex-1 items-center gap-2">
          <TeamSwitcher defaultTeam={teams[0]} teams={teams} />
        </div>
        {/* Middle area */}
        <AppToggle />
        {/* Right side */}
        <div className="flex flex-1 items-center justify-end gap-2">
          <Button
            className="text-sm max-sm:aspect-square max-sm:p-0"
            size="sm"
            variant="ghost"
          >
            <UploadIcon
              aria-hidden="true"
              className="sm:-ms-1 opacity-60"
              size={16}
            />
            <span className="max-sm:sr-only">Export</span>
          </Button>
          <Button className="text-sm max-sm:aspect-square max-sm:p-0" size="sm">
            <SparklesIcon
              aria-hidden="true"
              className="sm:-ms-1 opacity-60"
              size={16}
            />
            <span className="max-sm:sr-only">Upgrade</span>
          </Button>
        </div>
      </div>
    </header>
  );
}

"use client";

import { useId, useState } from "react";

import { RadioGroup, RadioGroupItem } from "@/registry/default/ui/radio-group";

export default function AppToggle() {
  const id = useId();
  const [selectedValue, setSelectedValue] = useState("on");

  return (
    <div className="inline-flex h-8 rounded-md bg-input/50 p-0.5">
      <RadioGroup
        className="group relative inline-grid grid-cols-[1fr_1fr] items-center gap-0 font-medium text-sm after:absolute after:inset-y-0 after:w-1/2 after:rounded-sm after:bg-background after:shadow-xs after:transition-[translate,box-shadow] after:duration-300 after:ease-[cubic-bezier(0.16,1,0.3,1)] has-focus-visible:after:border-ring has-focus-visible:after:ring-[3px] has-focus-visible:after:ring-ring/50 data-[state=off]:after:translate-x-0 data-[state=on]:after:translate-x-full"
        data-state={selectedValue}
        onValueChange={setSelectedValue}
        value={selectedValue}
      >
        <label className="relative z-10 inline-flex h-full min-w-8 cursor-pointer select-none items-center justify-center whitespace-nowrap px-3 transition-colors group-data-[state=on]:text-muted-foreground/70">
          Sitemap
          <RadioGroupItem className="sr-only" id={`${id}-1`} value="off" />
        </label>
        <label className="relative z-10 inline-flex h-full min-w-8 cursor-pointer select-none items-center justify-center whitespace-nowrap px-3 transition-colors group-data-[state=off]:text-muted-foreground/70">
          Wireframe
          <RadioGroupItem className="sr-only" id={`${id}-2`} value="on" />
        </label>
      </RadioGroup>
    </div>
  );
}

"use client";

import { ChevronsUpDown } from "lucide-react";
import * as React from "react";

import { Button } from "@/registry/default/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/registry/default/ui/dropdown-menu";

export default function TeamSwitcher({
  teams,
  defaultTeam,
}: {
  teams: string[];
  defaultTeam: string;
}) {
  const [selectedProject, setSelectedProject] = React.useState(defaultTeam);

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button className="p-0 hover:bg-transparent" variant="ghost">
          <span className="flex size-8 items-center justify-center rounded-full bg-primary text-primary-foreground">
            {selectedProject.charAt(0).toUpperCase()}
          </span>
          <div className="flex flex-col gap-0.5 leading-none">
            <span className="">{selectedProject}</span>
          </div>
          <ChevronsUpDown className="text-muted-foreground/80" size={14} />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="start">
        {teams.map((project) => (
          <DropdownMenuItem
            key={project}
            onSelect={() => setSelectedProject(project)}
          >
            {project}
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}
