"use client";

import { BoldIcon, ItalicIcon, LinkIcon } from "lucide-react";

import { Button } from "@/registry/default/ui/button";
import {
  InputGroup,
  InputGroupAddon,
  InputGroupTextarea,
} from "@/registry/default/ui/input-group";
import { Label } from "@/registry/default/ui/label";

export default function Particle() {
  return (
    <InputGroup>
      <InputGroupTextarea id="about" placeholder="Tell us about yourselfâ€¦" />
      <InputGroupAddon align="block-start">
        <Label className="text-foreground" htmlFor="about">
          About
        </Label>
        <div className="ml-auto flex items-center gap-1">
          <Button aria-label="Bold" size="icon-xs" variant="ghost">
            <BoldIcon className="size-4" />
          </Button>
          <Button aria-label="Italic" size="icon-xs" variant="ghost">
            <ItalicIcon className="size-4" />
          </Button>
          <Button aria-label="Link" size="icon-xs" variant="ghost">
            <LinkIcon className="size-4" />
          </Button>
        </div>
      </InputGroupAddon>
    </InputGroup>
  );
}
